        // TODO add your handling code here:
        int num;
    
      num=Integer.parseInt(txtnum.getText());
        for (int i=1;i<=10;i++){
          txttabla.append(num+"*"+i +"="+num*i+"\n");
    }//GEN-LAST:event_jButton1ActionPerformed
